// Frontend (React) - App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./App.css";

axios.defaults.baseURL = 'http://localhost:3000';

function App() {
  const [chatData, setChatData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [rating, setRating] = useState(5);
  const [taskList, setTaskList] = useState([]);
  const [chatHistory, setChatHistory] = useState([]);

  useEffect(() => {
    // Fetch initial chat data
    fetchChatData();
  }, []);

  const fetchChatData = async () => {
    try {
      const response = await axios.get('/api/chat-data');
      setChatData(response.data);
      if(response.data && response.data.length > 0){
        setTaskList(response.data[0].tasks || []);
        setChatHistory([response.data[0]]);
      }
    } catch (error) {
      console.error('Error fetching chat data:', error);
    }
  };

  const handleNext = async () => {
    // Save current rating
    try {
      // Move to next chat pair if available
      if (currentIndex < chatData.length - 1) {
        const nextIndex = currentIndex + 1;
        setCurrentIndex(nextIndex);
        setTaskList(chatData[nextIndex].tasks || []);
        setChatHistory((prevHistory) => [...prevHistory, chatData[nextIndex]]);
        setRating(5); // Reset rating to default value
      }
    } catch (error) {
      console.error('Error saving rating:', error);
    }
  };

  return (
    <div class="app-container">
      {/* Left Panel - Tasks */}
      <div class="column left">
        <h2>Task Completed</h2>
        <ul class="tasks-list">
          {taskList.map((task, index) => (
            <li key={index}>{task}</li>
          ))}
        </ul>
      </div>

      {/* Right Panel - Chat and Controls */}
      <div class="column right">
        {/* Chat Area */}
        <div class="chat-container">
          {chatHistory.map((chat, index) => (
            <div key={index} class="chat-pair">
              <div class="agent-message">
                <p>{chat.agentResponse}</p>
              </div>
              <div class="user-message">
                <p>{chat.userResponse}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Controls */}
        <div class="controls-container">
          <div class="rating-container">
            <p class="rating-label">Rate Agent Response</p>
            <input
              type="range"
              min="0"
              max="10"
              value={rating}
              onChange={(e) => setRating(parseInt(e.target.value))}
              class="rating-slider"
            />
            <div class="slider-labels">
              <span>{rating}</span>
            </div>
          </div>
          <button class="next-button"
            onClick={handleNext}
            disabled={currentIndex >= chatData.length-1}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
